import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    danhSachGheDangDat:[
        {soGhe:'A5',gia:75000},
        {soGhe:'A6',gia:75000}
    ]
}

const DatVeReducer = createSlice({
  name: 'DatVeReducer',
  initialState,
  reducers: {
    changeGhe:(state,action)=>{
        //Lấy dữ liệu gửi lên
        const gheClick = action.payload;
        // state.danhSachGheDangDat.push(gheClick)
        let index = state.danhSachGheDangDat.findIndex(gheClick=>gheClick.soGhe === action.ghe);
        if(index!==-1){
            state.danhSachGheDangDat.splice(index,1);
        }else{
            state.danhSachGheDangDat.push(gheClick);
        }

    }
  }
});

export const {changeGhe} = DatVeReducer.actions

export default DatVeReducer.reducer
