import React, { Fragment } from 'react';
import danhSachGhe from '../Data/danhSachGhe.json'

export default function HangGhe(props) {
    const renderGhe = ()=>{
        return props.hangGhe.danhSachGhe.map((ghe,index)=>{
            let cssGheDaDat = '';
            let disabled = 'false';
            if(ghe.daDat){
                cssGheDaDat = 'gheDuocChon';
                disabled = 'true'
            }
            return <button disabled className={`ghe ${cssGheDaDat}`} key={index}>
                {ghe.soGhe}
            </button>
        })
    }
  return (
    <div className='text-light mt-2 ml-4 text-left'>
        {props.hangGhe.hang} 

        {renderGhe()}

        </div>
  )
}
