import React, { Fragment } from "react";
import danhSachGhe from "../Data/danhSachGhe.json";
import { useSelector,useDispatch } from "react-redux";

export default function HangGhe(props) {
  const renderGhe = () => {
    return props.hangGhe.danhSachGhe.map((ghe, index) => {
      let cssGheDaDat = "";
      let disabled = false;
      if (ghe.daDat) {
        cssGheDaDat = "gheDuocChon";
        disabled = true;
      }
      // Xét trạng thái vé đang đặt
      let {danhSachGheDangDat} = useSelector(state =>state.DatVeReducer)


      return (
        <button
          disabled={disabled}
          className={`ghe ${cssGheDaDat}`}
          key={index}
        >
          {ghe.soGhe}
        </button>
      );
    });
  };
  const renderSoHang =()=>{
    return props.hangGhe.danhSachGhe.map((hang)=>{
        return <button className="rowNumber">
            {hang.soGhe}
        </button>
    })


  }
  const renderHangGhe = () => {
    if (props.soHangGhe === 0) {
      return <div style={{marginLeft:'20px'}}>
      {props.hangGhe.hang} {renderSoHang()}
    </div>
    } else {
      return (
        <div>
          {props.hangGhe.hang} {renderGhe()}
        </div>
      );
    }
  };
  return (
    <div className="text-light mt-2 ml-4 text-left">{renderHangGhe()}</div>
  );
}
