import React, { Fragment } from 'react';
import danhSachGhe from '../Data/danhSachGhe.json'

export default function HangGhe(props) {
    const renderGhe = ()=>{
        return props.hangGhe.danhSachGhe.map((ghe,index)=>{
            if(index===0){
                return <span className='rowNumber'>
                    {ghe.soGhe}
                </span>
            }


            let cssGheDaDat = '';
            let disabled = false;
            if(ghe.daDat){
                cssGheDaDat = 'gheDuocChon';
                disabled = true;
            }
            return <button disabled={disabled} className={`ghe ${cssGheDaDat}`} key={index}>
                {ghe.soGhe}
            </button>
        })
    }
  return (
    <div className='text-light mt-2 ml-4 text-left'>
        {props.hangGhe.hang} 

        {renderGhe()}

        </div>
  )
}
