import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    danhSachGheDangDat:[
        {soGhe:'A5',gia:75000},
        {soGhe:'A6',gia:75000}
    ]
}

const DatVeReducer = createSlice({
  name: 'DatVeReducer',
  initialState,
  reducers: {
    changeGhe:(state,action)=>{
        //Lấy dữ liệu gửi lên
        const gheClick = action.payload;
        let danhSachGheDangDatUpdate = [...danhSachGheDangDat]
        let index = gheClick.findIndex(gheDangDat=>gheDangDat.soGhe===action.ghe.soGhe);
        if(index !== -1){
            danhSachGheDangDat.splice(index,1)
        }
        return {...state}
    }
  }
});

export const {changeGhe} = DatVeReducer.actions

export default DatVeReducer.reducer
