            <div className="text-light mt-2 text-center" style={{fontSize:'30px'}}>DANH SÁCH GHẾ BẠN CHỌN</div>
            <ThongTinDatGhe/>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
